          // if treatment changed or a next visit scheduled
          if (
            startDate === setDays(new Date(), -1) &&
            (treatment !== originalTreatment ||
              firstVisitDate !== lastVisitDate)
          ) {
            let alert = [];
            // Ignore scheduling for when treatment (SMS_treatment__c) equals 'Complete'
            if (treatment !== 'Complete') {
              // Schedule red conditions
              alert = Object.values(treatmentMapSchedule).filter(
                obj => obj.treatment === treatment
              );
              alertsToSend.push(...alert);
            }

            // Schedule teal conditions - alert 14, 15, 16
            alert = Object.values(treatmentMapSchedule).filter(
              obj => obj.braceProblemsType === braceProblemType
            );
            alertsToSend.push(...alert);
          }
          // Scheduling alert #13 for when Treatment_Completed__c is 'true'
          if (treatmentCompleted == true && treatmentCompletedDate === setDays(new Date(), -1)) {
            alertsToSend.push(treatmentMapSchedule['complete']);
          }
        }
        // DELETION FINAL FLOW =====================================================
        // If treatment completed, we go delete previously scheduled SMSs
        // TODO: To determine if we can simplify this flow and determine WHICH treatment was completed
        if (treatmentCompleted == true) {
          let alert = [];
          alert = Object.values(treatmentMapSchedule).filter(
            obj => obj.treatment === originalTreatment
          );
          alertsToDisable.push(...alert);
          alertsToDisable.push(
            treatmentMapSchedule['not_wearing_enough'],
            treatmentMapSchedule['child_not_tolerating'],
            treatmentMapSchedule['family_not_accepting'],
            treatmentMapSchedule['reminder_before'],
            treatmentMapSchedule['reminder_after']
          );

          if (treatment !== 'Complete') {
            let alert = [];
            alert = Object.values(treatmentMapSchedule).filter(
              obj => obj.treatment === treatment
            );
            alertsToDisable.push(...alert);
          }
        }
        //Now if the treatment has changed since last run..
        if (
          (startDate === setDays(new Date(), -1) &&
            treatment !== originalTreatment) ||
          reasonStoppedTreatment !== null
        ) {
          //we assume if they provided a reason, then they stopped treatment
          // ...and treatment included in treatmentsList then delete originalTreatment...
          // ...or brace problems type...
          // ...or treatment is completed.
          if (
            treatmentsList.includes(treatment)
            // reasonStoppedTreatment !== null
          ) {
            let alert = [];
            alert = Object.values(treatmentMapSchedule).filter(
              obj => obj.treatment === originalTreatment
            );
            alertsToDisable.push(...alert);
            alertsToDisable.push(
              treatmentMapSchedule['not_wearing_enough'],
              treatmentMapSchedule['child_not_tolerating'],
              treatmentMapSchedule['family_not_accepting']
            );
          }
          if (
            // Now if treatment suspended, stopped or SMS opted out...
            // Then cancel Visit Reminder and Treatment SMSs
            // treatment === 'Complete' || ignore deletion for when treatment (SMS_treatment__c) equals 'Complete'
            treatment === 'Suspended' ||
            reasonStoppedTreatment !== null ||
            smsOptIn === false // Opt-out
          ) {
            alertsToDisable.push(treatmentMapSchedule['reminder_before']);
            alertsToDisable.push(treatmentMapSchedule['reminder_after']);
            if (
              treatment === 'Casting' ||
              treatment === 'Tenotomy' ||
              treatment === 'Bracing Day' ||
              treatment === 'Bracing Night'
            ) {
              let alert = [];
              alert = Object.values(treatmentMapSchedule).filter(
                obj => obj.treatment === treatment
              );
              alertsToDisable.push(...alert);
            }
            if (treatment === 'Bracing Day' || treatment === 'Bracing Night') {
              alertsToDisable.push(
                treatmentMapSchedule['not_wearing_enough'],
                treatmentMapSchedule['child_not_tolerating'],
                treatmentMapSchedule['family_not_accepting']
              );
            }
          }
        }
        // ========================================================================
        console.log('treatment', alertsToSend);

        if (alertsToSend.length > 0) {
          // for each alert to send
          for (let alert of alertsToSend) {
            console.log('============= START SMS SCHEDULE =============');
            const { key, bulkPrefix } = alert;
            for (let rule of mapping[key]) {
              console.log('=======================================');
              console.log(`rule ${key} 'Schedule Start Date (SSD):`, startDate);

              const start_date = startDate;
              console.log('Start date fetched: ', start_date);

              if (!start_date) {
                console.log(
                  'Skipping SMS scheduling because alert date is empty'
                );
                break;
              }

              let sendAtDate = new Date(start_date);

              // We build the bulkId for this alert from the case type the `# SMS` and the `@case_id`
              let bulkId = `${bulkPrefix}${rule['# SMS']}-${caseId}`;
              if (bulkPrefix === 'visitAfter-') {
                bulkId = `${bulkId}-${Next_Visit_Date_New__c}`;
                sendAtDate = new Date(Next_Visit_Date_New__c);
              }

              if (bulkPrefix === 'visitBefore-') {
                bulkId = `${bulkId}-${start_date}`;
                sendAtDate = new Date(Next_Visit_Date_New__c);
              }

              console.log('bulkId: ', bulkId);
              // console.log('rule', rule[languageCodeMap[language_code]]);

              // We build the sms from the rule and the information from the contact
              const sms = rule[languageCodeMap[language_code]]
                .map((item, pos) => (pos % 2 === 0 ? item : contact[item]))
                .join('');

              sendAtDate.setDate(sendAtDate.getDate() + rule['Days from SSD']);
              const hours = setHoursFromRule(rule);
              const minutes = setMinutesFromRule(rule);

              sendAtDate.setHours(parseInt(hours));
              sendAtDate.setMinutes(
                parseInt(minutes) +
                (rule['Min From SSD'] ? rule['Min From SSD'] : 0)
              );

              // Adding timezone offset ====================================
              const dateBeforeTZ = sendAtDate;
              // const timezone = countryToTimeZone[Country];
              const timezone = sms_timezone;
              if (timezone !== '') {
               let sendAtDateTimeZone = [
                  sendAtDate
                    .toISOString()
                    .substring(0, sendAtDate.toISOString().length - 1),
                  timeZoneMap[timezone],
                ].join('');
                sendAtDate = new Date(sendAtDateTimeZone);
              }
              console.log('before timezone', dateBeforeTZ.toISOString());
              console.log('after timezone', sendAtDate.toISOString());
              // ============================================================

              // Delay sending date =========================================
              // If message risks to be sent between 8PM and 8AM delay until 8AM the next day.
              // We do not want to send SMSs outside of normal daytime hours
              if (sendAtDate.getHours() >= 20) {
                sendAtDate.setDate(sendAtDate.getDate() + 1);
              }
              if (sendAtDate.getHours() >= 20 || sendAtDate.getHours() < 8) {
                sendAtDate.setHours(8);
              }

              const currentHours = sendAtDate.getHours();
              const currentMinutes = sendAtDate.getMinutes();

              if (bulkPrefix === 'visitAfter-' || bulkPrefix === 'visitBefore-') {
                if ((new Date() - sendAtDate) / (1000 * 60 * 60 * 24) > 7) {
                  // If sendAtDate is more than 2 days in the past, skip to the next SMS
                  console.log("Visit reminder date in the past, not scheduling")
                  break;
                }
              }

              if ((sendAtDate - new Date()) / (1000 * 60 * 60 * 24) > 180) {
                // If sendAtDate is more than 180 days in the future, skip to the next SMS
                console.log("Visit reminder date more than 6 months in the future, not scheduling")
                break;
              }

              // If messages was supposed to be sent in the past, send it on day of the run at same time
              if (sendAtDate < new Date()) {
                sendAtDate.setDate(new Date().getDate());
                sendAtDate.setHours(currentHours);
                sendAtDate.setMinutes(currentMinutes);
              }
              // ============================================================

              const sendAt = sendAtDate.toISOString();
              const to = Phone;
              console.log('sending to', to);
              if (!to) {
                console.log(
                  'No phone number defined! Skipping SMS scheduling.'
                );
                break;
              }
              console.log('Sending SMS at: ', sendAt);

              const from = PhoneMapping[patientCountry];
              const message = {
                from,
                destinations: [
                  {
                    to,
                  },
                ],
                text: sms,
                callbackData: caseId,  //For easier analysis in Infobip reports
                sendAt,
              };
              messagesToSend.push({
                bulkId,
                message,
              });
              // END Send SMS ================================================
            }
          }
        }
        if (alertsToDisable.length > 0) {
          // for each alert to cancel
          for (let alert of alertsToDisable) {
            console.log('============= START SMS CANCELATION =============');
            const { key, bulkPrefix } = alert;
            for (let rule of mapping[key]) {
              let bulkId = `${bulkPrefix}${rule['# SMS']}-${caseId}`;
              let alternateBulkId = `${bulkPrefix}${rule['# SMS']}-${caseId}`;


              if (bulkPrefix === 'visitAfter-') {
                bulkId = `${bulkId}-${lastVisitDate}`;
                alternateBulkId = `${bulkId}-${originalNextVisitDate}`;
              }
              if (bulkPrefix === 'visitBefore-') {
                bulkId = `${bulkId}-${startDate}`;
              }

              messagesToCancel.push(bulkId);
              messagesToCancel.push(alternateBulkId);
              console.log('bulkId to delete: ', bulkId, alternateBulkId);
            }
          }
        }
      }
      if (!contact.sendSms ||contact.sms_error !== null) {
        // If SMS is turned off, delete all scheduled alerts 
        const alertsToDisable = []; // this will hold the list of alerts to delete for this contact.
        console.log('contact to delete sms for', contact);

        // We organize destructuring by concern.
        const { caseId } = contact; // destructuring contact info
        const { treatment } = contact; // destructuring treatment info
        const { startDate, lastVisitDate, originalNextVisitDate } = contact; // destructuring dates

        if (
          treatment === 'Casting' ||
          treatment === 'Tenotomy' ||
          treatment === 'Bracing Day' ||
          treatment === 'Bracing Night'
        ) {
          let alert = [];
          alert = Object.values(treatmentMapSchedule).filter(
            obj => obj.treatment === treatment
          );
          alertsToDisable.push(...alert);
          alertsToDisable.push(treatmentMapSchedule['reminder_before']);
          alertsToDisable.push(treatmentMapSchedule['reminder_after']);
        }
        if (treatment === 'Bracing Day' || treatment === 'Bracing Night') {
          alertsToDisable.push(
            treatmentMapSchedule['not_wearing_enough'],
            treatmentMapSchedule['child_not_tolerating'],
            treatmentMapSchedule['family_not_accepting']
          );
        }

        if (alertsToDisable.length > 0) {
          // for each alert to cancel
          for (let alert of alertsToDisable) {
            console.log('============= START SMS CANCELATION =============');
            const { key, bulkPrefix } = alert;
            for (let rule of mapping[key]) {
              let bulkId = `${bulkPrefix}${rule['# SMS']}-${caseId}`;
              let alternateBulkId = `${bulkPrefix}${rule['# SMS']}-${caseId}`;

              if (bulkPrefix === 'visitAfter-') {
                bulkId = `${bulkId}-${lastVisitDate}`;
                alternateBulkId = `${bulkId}-${originalNextVisitDate}`;

              }
              if (bulkPrefix === 'visitBefore-') {
                bulkId = `${bulkId}-${startDate}`;
              }

              messagesToCancel.push(bulkId);
              messagesToCancel.push(alternateBulkId);
              console.log('bulkId to delete: ', bulkId, alternateBulkId);
            }
          }
        }
      }
    }
  }

  // This log all the messages (to send and to bulkIds to delete) for all contacts
  console.log('messages to send', JSON.stringify(messagesToSend, null, 2));
  console.log('messages to delete', JSON.stringify(messagesToCancel, null, 2));

  return {
    ...state,
    messagesToSend,
    messagesToCancel,
  };
});

fn(async state => {
  const { host, token } = state.configuration;

  function getSMS(bulkId) {
    return get(
      `${host}/1/bulks/status?bulkId=${bulkId}`,
      {
        headers: {
          'Content-Type': 'application/json',
          Authorization: `App ${token}`,
        },
        options: {
          successCodes: [200, 404],
        },
      },
      state => {
        const response = state.data;
        if (
          response.bulkId ||
          (response.requestError &&
            response.requestError.serviceException &&
            response.requestError.serviceException.messageId === 'NOT_FOUND')
        ) {
          return response;
        }
        return new Error(response);
      }
    )(state);
  }

  function scheduleSMS(bulkId, message) {
    return post(`${host}/2/text/advanced`, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `App ${token}`,
      },
      body: {
        bulkId,
        messages: [message],
      },
    })(state);
  }

  function rescheduleSMS(bulkId, sendAt) {
    return put(`${host}/1/bulks?bulkId=${bulkId}`, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `App ${token}`,
      },
      body: {
        sendAt,
      },
    })(state);
  }

  function deleteSMS(bulkId) {
    return put(`${host}/1/bulks/status?bulkId=${bulkId}`, {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `App ${token}`,
      },
      body: {
        status: 'CANCELED',
      },
    })(state);
  }

  const { messagesToSend, messagesToCancel } = state;

  // SCHEDULE SMS PROCESS =======================================================
  for (let messageToSend of messagesToSend) {
    const { bulkId, message } = messageToSend;
    console.log('============= START SMS SCHEDULE =============');
    // Send SMS ====================================================
    console.log(`Check for existing scheduled SMS for ${bulkId}...`);
    await getSMS(bulkId).then(res => {
      if (res.requestError) {
        console.log(
          `Existing SMS not found. Scheduling SMS for ${bulkId} at ${message.sendAt}...`
        );
        console.log('Sending message:', message);
        return scheduleSMS(bulkId, message);
      } else {
        if (res.status === 'FINISHED' || res.status === 'CANCELED') {
          console.log(
            'SMS is already canceled or sent, impossible to reschedule!'
          );
          return state;
        }

        console.log(
          `SMS already scheduled. Rescheduling for ${bulkId} at ${message.sendAt}...`
        );
        console.log('Sending message:', message);
        return rescheduleSMS(bulkId, message.sendAt);
      }
    });
    // END Send SMS ================================================
    console.log('=======================================\n');
  }
  // ============================================================================

  // DISABLE SMS PROCESS ========================================================
  for (let bulkId of messagesToCancel) {
    console.log('============= START SMS CANCELATION =============');
    console.log(`Check for existing scheduled SMS for ${bulkId}`);
    await getSMS(bulkId).then(res => {
      if (
        !res.requestError &&
        res.status !== 'FINISHED' &&
        res.status !== 'CANCELED'
      ) {
        console.log('Deleting SMS.');
        return deleteSMS(bulkId);
      } else {
        console.log('SMS is already canceled or sent!');
        return state;
      }
    });
    console.log('============= END SMS CANCELATION =============');
  }
  // ============================================================================

  return state;
});

